import React, { useEffect } from "react";
import { LinkContainer } from "react-router-bootstrap";
import { Table, Button,Row,Col } from "react-bootstrap";
import { Loader } from "../Loader";
import Message from "../Message";
import { useDispatch, useSelector } from "react-redux";
import { deleteProduct, listProducts,createProduct } from "../../actions/productActions";
import { PRODUCT_CREATE_RESET } from "../../constants/productConstants";

const ProductListScreen = ({history,match}) => {
  const dispatch = useDispatch();


  const productList = useSelector((state) => state.productList);
  const { loading, error, products } = productList;


  const productDelete = useSelector((state) => state.productDelete);
  const { loading:loadingDelete, error:errorDelete, success:successDelete} = productDelete;



  const productCreate = useSelector((state) => state.productCreate);
  const { loading:loadingCreate, error:errorCreate, success:successCreate,product:createdProduct} = productCreate;

  const userLogin = useSelector((state) => state.userLogin);
  const { userInfo } = userLogin;



  useEffect(() => {
    dispatch({type:PRODUCT_CREATE_RESET}) 

    if(!userInfo.isAdmin){
        history.push('/login') 
    }


  }, [dispatch,history,userInfo,successDelete]);


  const deleteHandler =(id)=>{
      if(window.confirm('Are you sure you want to delete this product')){
        dispatch(deleteProduct(id))
      }

  }
  const createProductHandler=(product)=>{
    //create product
  }

  return (
    <div>
    <Row className='align-items-center'>
        <Col>
        <h1>Products</h1>
        </Col>

        <Col className='text-right'>
        <Button className='my-3' onClick={createProductHandler} size="sm">
           <i className='fas fa-plus'> create Product</i> 
        </Button>
        </Col>

    </Row>
    {loadingDelete && <Loader/>}
    {errorDelete && <Message variant='danger'>{errorDelete}</Message>}
      {loading ? (
        <Loader />
      ) : error ? (
        <Message variant="danger">{error}</Message>
      ) : (
        <Table stripped bordered hover responsive className='table-sm'>
           <thead>
               <tr>
               <th>ID</th>
               <th>NAME</th>
               <th>PRICE</th>
               <th>CATEGORY</th>
               <th>BRAND</th>
               <th></th>
               </tr>
               </thead>
               <tbody>
                   {products.map(product => (
                       <tr key={product._id}>
                       <td>{product._id}</td>    
                       <td>{product.name}</td>
                       <td>${product.price}</td>
                       <td>{product.category}</td>
                       <td>{product.brand}</td>


                       <td>
                           <LinkContainer to={`/admin/product/${product._id}/edit`}>
                               <Button variant='light' className='btn-sm'><i
                           className='fas fa-edit'>

                           </i>Edit</Button>
                           </LinkContainer>

                           <Button variant='danger' className='btn-sm' onClick={()=>deleteHandler(product._id)}><i
                           className='fas fa-trash'>

                           </i>Delete</Button>
                       </td>

                       </tr>

                   ))}
               </tbody>

        </Table>
      )}
    </div>
  );
};

export default ProductListScreen;
